// src/Layout.js
import React from 'react';
import Header from '../../Common/mainHeader.js'; // Adjust the path as necessary
import Footer from '../Common/Footer'; // Adjust the path as necessary
import Router from '../../Router/Routes.js'; // Adjust the path as necessary

const Layout = ({ userRole }) => {
    // You can conditionally render different headers based on userRole here
    const renderHeader = () => {
        switch (userRole) {
            case 'admin':
                return <Header title="Admin Dashboard" />;
            case 'user':
                return <Header title="User  Dashboard" />;
            default:
                return <Header title="Welcome" />;
        }
    };

    return (
        <>
            {renderHeader()}
            <Router />
            <Footer />
        </>
    );
}

export default Layout;
